/* mySeedScript.js */

// require the necessary libraries
const faker = require("faker");
const MongoClient = require("mongodb").MongoClient;

function randomIntFromInterval(min, max) {
  // min and max included
  return Math.floor(Math.random() * (max - min + 1) + min);
}

async function seedDB() {
  // Connection URL
  const uri =
    "mongodb+srv://harrisahmad55:ladBob12@cluster0.4d91bik.mongodb.net/guftaar";

  const client = new MongoClient(uri, {
    useNewUrlParser: true,
    // useUnifiedTopology: true,
  });

  try {
    await client.connect();
    console.log("Connected correctly to server");

    const collection = client.db("guftaar").collection("coach");

    // The drop() command destroys all data from a collection.
    // Make sure you run it against proper database and collection.
    // collection.drop();

    // make a bunch of time series data
    let timeSeriesData = [];

    for (let i = 0; i < 5; i++) {
      const firstName = faker.name.firstName();
      const lastName = faker.name.lastName();
      const email = faker.internet.email();
      const age = randomIntFromInterval(20, 60);
      const yearsOfExperience = randomIntFromInterval(1, 10);
      const qualification = faker.name.jobTitle();
      const gender = "M";
      const password = faker.internet.password();
      let newDay = {
        firstName: firstName,
        lastName: lastName,
        email: email,
        age: age,
        yearsOfExperience: yearsOfExperience,
        qualification: qualification,
        gender: gender,
        password: password,
      };
      console.log(newDay);
      timeSeriesData.push(newDay);
    }
    collection.insertMany(timeSeriesData);
    console.log("Database seeded! :)");
    client.close();
  } catch (err) {
    console.log(err.stack);
  }
}

seedDB();
